/**
 * Flow handlers exports
 */

// Base flow handler
export { BaseFlowHandler, SimpleFlowHandler, FlowHandlerFactory } from './FlowHandler';

// Built-in flow handlers
export { 
  AuthorizationCodeFlowHandler, 
  createAuthorizationCodeFlowHandler 
} from './AuthorizationCodeFlowHandler';

export { 
  MagicLinkFlowHandler,
  MagicLinkLoginFlowHandler,
  MagicLinkRegistrationFlowHandler,
  createMagicLinkFlowHandler,
  createMagicLinkLoginFlowHandler,
  createMagicLinkRegistrationFlowHandler
} from './MagicLinkFlowHandler';

// Flow types
export * from '../types/FlowTypes';

// Convenience function to create all built-in flow handlers
export function createBuiltInFlowHandlers() {
  return [
    new AuthorizationCodeFlowHandler(),
    new MagicLinkFlowHandler(),
  ];
}

// Convenience function to create specialized magic link handlers
export function createMagicLinkFlowHandlers() {
  return [
    new MagicLinkLoginFlowHandler(),
    new MagicLinkRegistrationFlowHandler(),
  ];
}
